SELECT t.id, t.storeid, t.billno, t.orderpsnid, t.serviceprojectid, c.name service_name, t.ordertime, t.carinfoid, b.platnumber car_no, t.customerid, a.name customer_name, t.source, t.status, t.meno, t.creator, t.createtime, t.modifier, t.modifiedtime FROM order_project t left join customer a on t.customerid=a.id left join car_info b on t.carinfoid=b.id left join service_product c on t.serviceprojectid=c.id where 1=1
and t.id=#{id}
and t.customerid=#{customerId}
and t.status=#{status}
and t.billno=#{billno}
and t.carinfoid=#{carInfoId}
