mapper.select:
	    SELECT
          t.id,
          t.code,
          t.name,
          t.storeid,
          t.parentid,
          t.creator,
          t.createtime,
          t.modifier,
          t.modifiedtime
        FROM  product_class t where 1=1
           
              and t.id=#{id}
           
           
              and t.code=#{code}
           
           
              and t.storeid=#{storeId}
                       
           
              and t.parentid=#{parentId}
                      
           
              and t.name=#{name}

SELECT t.id, t.code, t.name, t.storeid, t.parentid, t.creator, t.createtime, t.modifier, t.modifiedtime FROM product_class t where 1=1
and t.id=#{id}
and t.storeid=#{storeId}
and t.code=#{code}
and t.name=#{name}

		select
		   count(1)
		from product_class t where 1=1
           
              and t.id=#{id}
           
           
              and t.storeid=#{storeId}
                      
           
              and t.code=#{code}
           
           
              and t.name=#{name}

SELECT t.id, t.code, t.name, t.storeid, t.parentid, t.creator, t.createtime, t.modifier, t.modifiedtime FROM product_class t where 1=1
and t.id=#{id}
and t.code=#{code}
and t.name=#{name}
